import React, { Component } from 'react'
import jwt_decode from 'jwt-decode';
import axios from 'axios';
import Card from './reservationCard'


export default class customerReservation extends Component {
    constructor(props) {
        super();

        this.state = {
            reservations: []
        }

    }

    componentDidMount() {
        const token = localStorage.usertoken
        const decoded = jwt_decode(token)
        const customerID = decoded.id

        axios.get('http://localhost:5000/reservation/' + customerID)
            .then(response => {
                // console.log(response.data)
                const reservation = response.data
                this.setState({ reservations: reservation });
            }).catch(error => {
                console.log("error come from reservation display:" + error)
            })

    }

    ReservationList() {
        return this.state.restaurants.map(currentrestaurant => {
            return <Card key={currentrestaurant.rid} user={currentrestaurant} />;
        })
    }



    render() {
        return (

            <div>
                <div>page to display the reservation</div>
                {this.ReservationList()}
            </div>
        )
    }
}
