import React, { Component } from 'react';
import axios from 'axios';
import Card from './RestaurantCard';


export default class LocationMap extends Component {

    constructor() {
        super();

        this.state = { 
            r_name:'',
            r_address:'',
            r_phone:'',
            r_pic:'',
            r_desciption:''
         };
    }

    componentDidMount() {
        const token = localStorage.usertoken
        const decoded = jwt_decode(token)
        this.setState({
            r_name: decoded.r_name,
            r_address:decoded.r_address,
            r_phone:decoded.r_phone,
            r_pic:decoded.pic,
            r_desciption:decoded.r_desciption
        })

    }

    // RestaurantList() {
    //     return this.state.users.map(currentRestaurant => {
    //         return <Card user={currentRestaurant} key={currentRestaurant._id} />;
    //     })
    // }

    render() {
        return (
            <div className="col-md-6">
                <div className="card businessCard" >
                    <div className="card-body">
                        <img src={this.state.r_pic} alt={this.state.r_name}/>
                        <h1>{this.state.r_name}</h1>
                        <h6>{this.state.r_phone}</h6>
                        <h6>{this.state.r_address}</h6>  
                        <p className="card-text">about:{this.state.r_desciption.split("\n").map(function (item) {
                            return (
                                <span>
                                    {item}
                                    <br />
                                </span>
                            )
                        })}</p>
                    </div>
                </div>
            </div>
        )
    }
}
