import React, { Component } from 'react';
import jwt_decode from 'jwt-decode';
import axios from 'axios';
import Card from './FavoritePage/FavoriteCard'



export default class Favorite extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            favorites: [],
            resturants :[] 
        }
    }

    componentDidMount() {
        const token = localStorage.usertoken
        const decoded = jwt_decode(token)

        axios.get('http://localhost:5000/users/favorite/' + decoded.id)
            .then(response => {
                // console.log(response.data)
                const favorites = response.data;
                this.setState({ favorites });
            })
            .catch(error => {
                console.log(error)
            })
    }

    GetRestaurant(rid) {
        return axios.get('http://localhost:5000/restaurants/'+rid)
            .then(response => {
                const restaurant = response.data
                this.setState({resturant})
                console.log(restaurant)
            })
    }

    FavoriteList(){
        return this.state.favorites.map(currentfavorite =>{
            this.GetRestaurant(currentfavorite.rid)
        });
    }




    // FavoriteList(){
    //     return this.state.favorites.map(currentfavorite =>{
    //         return <Card key = {currentfavorite.event_id} favor= {currentfavorite}/>
    //     });
    // }

    render() {
        return (
            <div>
                <h1>this is the page for user Restaurant Profile</h1>
                <div className='row'>
                    {this.FavoriteList()}
                </div>

            </div>
        );
    }
}
