import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
import Card from './RestaurantCard';



export default class NewsEvent extends Component {

    constructor(props) {
        super(props); 
        this.state = { restaurants: [] };
    }

    //got data from the database
    componentDidMount() {
        axios.get('http://localhost:5000/restaurants/eventdispaly')
            .then(response => {
                console.log(response.data)
                const restaurants = response.data;
                this.setState({restaurants});
            })
            .catch((error) => {
                console.log(error);
            })
    }

    // companiesList() {
    //     return this.state.companies.map(currentCompany => {
    //         return <Card user={currentCompany}  />;
    //     })
    // }

    render() {
        return (
            <div className = "container">
                <h3>Company List</h3>
                <div className="row">
                        {this.companiesList()}           
                </div>
            </div>
        );
    }
}
