import React, { Component } from 'react';
import axios from 'axios';
import Card from './searcheventCard';


export default class NewsEvent extends Component {

    constructor(props) {
        super(props);
        this.state =
        {
            eventsearch: [],
            search: ""
        };
    }

    //got data from the database
    componentDidMount() {

        axios.get('http://localhost:5000/restaurants/events/allevent')
            .then(response => {
                console.log(response.data)
                const eventsearch = response.data;
                this.setState({ eventsearch: eventsearch });
            })
            .catch((error) => {
                console.log(error);
            })
    }

    updateSearch(e) {
        this.setState({ search: e.target.value });
    }


    // allEventList() {
    //     let filteredEvents = this.state.eventsearch.filter(
    //         eventsearch => {
    //             return eventsearch.indexOf(this.state.search)!= -1;
    //         }
    //     )
    //     return filteredEvents.map(currentEvent => {
    //         return <Card key={currentEvent.event_id} event={currentEvent} />;
    //     })
    // }

    render() {
        let filteredEvents = this.state.eventsearch.filter(
            (eventsearch) => {
                // return eventsearch.event_name.indexOf(this.state.search) !== -1;
            }
        );
        return (
            <div>
                <input type="text" value={this.state.search} onChange={this.updateSearch.bind(this)} placeholder="Search.." />
                {/* <div>{this.allEventList()}</div> */}
                <div>
                    {filteredEvents.map((eventsearch)=>{
                        return <Card key={eventsearch.event_id} event={eventsearch} />
                    })}
                </div>

            </div>
        );
    }
}
