import React from 'react';
import { Link } from 'react-router-dom';

function RestaurantCard(props) {
    return (
        <div className = "col-md-6">

            <div className="card businessCard" >
                <div className="card-body">
                    <p className="card-title companyTitle" >{props.company.CompanyName}</p>
                    <h6 className ="card-subtitle mb-2 text-muted">{props.company.JobTitle}</h6>
                    <label>Require skill:</label>
                    <p className="card-text">{props.company.JobRequire}</p>
                    {/* <p className="card-text">about: {props.company.CompanyDescribe}</p> */}
                    <p className="card-text">about:{props.company.CompanyDescribe.split("\n").map(function(item) {
                          return (
                                <span>
                                    {item}
                                <br/>
                                </span>
                                )
                    })}</p> 
                    <p className="card-text jobstatus">{props.company.JobStatus}</p>
                    <p className="card-text">{props.company.date.substring(0, 10)}</p>
                    <Link to={"/edit/" + props.company._id}>edit</Link> | <a href='#' onClick={() => { props.deleteCompany(props.company._id) }} >delete</a>

                </div>
            </div>

        </div>
    )

}
export default CompanyCard;
