    constructor(props) {
        super(props);
        this.state = { users: [] };
    }

    componentDidMount() {
        this.getUsers();
    }

    getUsers = _ => {
        fetch('http://localhost:3001')
            .then(response => console.log(response))//response.json())
            .then(({ response }) => this.setState({ users: 'response.users' }))
            .catch(error => console.log(error));
    }
    showUsers = user => { user.r_name }



