import React, { Component } from 'react';
import axios from 'axios';
import Card from './RestaurantCard';


export default class LocationMap extends Component {

    constructor(props) {
        super(props);

        this.state = { users: [] };
    }

    componentDidMount() {
        axios.get('http://localhost:5000/resturants')
            .then(response => {
                this.setState({ user: response.data });
            })
            .catch((error) => {
                console.log(error);
            })
    }

    companiesList() {
        return this.state.companies.map(currentCompany => {
            return <Card company={currentCompany} deleteCompany={this.deleteCompany} key={currentCompany._id} />;
        })
    }

    render() {
        return (
            <div className = "container">
                <h3>Company List</h3>
                <div className="row">
                        {this.companiesList()}           
                </div>
            </div>
        );
    }
}
