import React, { Component } from 'react';
import jwt_decode from 'jwt-decode';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";


export default class makereservation extends Component {
    constructor(props) {
        super(props);

        this.onChangeFirstName = this.onChangeFirstName.bind(this)
        this.onChangeLastName = this.onChangeLastName.bind(this)
        this.onChangeEmail = this.onChangeEmail.bind(this)
        this.onChangePhone = this.onChangePhone.bind(this)
        this.onChangePassword = this.onChangePassword.bind(this)
        this.onChangeConform = this.onChangeConform.bind(this)
        this.onSubmit = this.onSubmit.bind(this)

        this.state = {
            cid: "",
            rid: "",
            date: "",
            time: "",
            seats: "",
            notes: "",
            rating: "",
            comment: ""
        }
    }

    componentDidMount() {
        const token = localStorage.usertoken;
        const decoded = jwt_decode(token);

        this.setState({
            cid: decoded.id,
            rid: this.props.match.params.id,
        })
    }

    onChangeFirstName(e) {
        this.setState({
            first_name: e.target.value
        });
    }

    onChangeLastName(e) {
        this.setState({
            last_name: e.target.value
        });
    }

    onChangeEmail(e) {
        this.setState({
            email: e.target.value
        });
    }

    onChangePhone(e) {
        this.setState({
            phone: e.target.value
        });
    }

    onChangePassword(e) {
        this.setState({
            password: e.target.value
        });
    }

    onChangeConform(e) {
        this.setState({
            conform: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const usersingup = {
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            email: this.state.email,
            phone: this.state.phone,
            password: this.state.password,
        }


        // axios
        //     .post('http://localhost:5000/users/register', usersingup)
        //     .then(response => {
        //         console.log('Registered')
        //     })
        //     .catch(err => {
        //         console.log(err.response.data.error)
        //         this.setState({errorMessage:err.response.data.error})
        //     })

        //window.location = "/"

        //  TODO : send data to the database

    }


    render() {

        return (
            <div className="container">


                <form onSubmit={this.onSubmit}>
                    <div>
                        <label >reservation date</label>
                        <DatePicker
                            dayPickerProps={{
                                month: new Date(2018, 10),
                                showWeekNumbers: true,
                                todayButton: 'Today',
                            }}
                            selected={this.state.event_date}
                            onChange={this.onChangeDate}
                        />
                    </div>


                </form>
            </div>
        );
    }

}
