import React, { Component } from 'react';
import axios from 'axios';



export default class EventCreate extends Component {

    constructor(props) {
        super(props);

        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)

        this.state = {
            rid: '',
            event_title: '',
            date: '',
            start_time: '',
            end_time: '',
            description: '',
            event_picture: ''
        }

    }

    onChange(e) {
        this.setState({ [e.target.name]: e.target.value })
    }
    onSubmit(e) {
        e.preventDefault();

        const eventcreate = {
            rid: this.state.rid,
            event_title: this.state.event_title,
            start_time: this.state.start_time,
            end_time: this.state.end_time,
            description: this.state.description,
            event_picture: this.state.event_picture
        }

        axios.post('http://localhost:5000/restaurants/events/create', { eventcreate })
            .then(res => {
                console.log(res);
                console.log(res.data);
            })
        window.location = "/user/profile"
    }


    render() {
        return (
            <div className="container">
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <input type="text"
                            placeholder="event_title"
                            className="form-control"
                            value={this.state.event_title}
                            onChange={this.onChange} />
                    </div>
                    <label >Event start time</label>
                    <input type="time" name="appt" required></input>
                    <label for="appt">Event end time</label>
                    <input type="time" id="appt" name="appt" required></input>

                    <div className="form-group">
                        <input type="text"
                            placeholder="event description"
                            className="form-control"
                            value={this.state.description}
                            onChange={this.onChange} />
                    </div>

                    <div className="form-group">
                        <input type="text"
                            placeholder="event_title"
                            className="form-control"
                            value={this.state.event_picture}
                            onChange={this.onChange} />
                    </div>
                </form>
            </div>
        )
    }
}
