import React from 'react';
import { Link } from 'react-router-dom';

function RestaurantCard(props) {
    return (
        <div className="col-md-6">

            <div className="card businessCard" >
                <div className="card-body">
                    <img src={props.users.r_pic} alt={props.users.r_name}/>
                    <h1>{props.users.r_name}</h1>
                    <h6>{props.users.r_phone}</h6>
                    <h6>{props.users.r_address}</h6>

                    <p className="card-text">about:{props.users.r_desciption.split("\n").map(function (item) {
                        return (
                            <span>
                                {item}
                                <br />
                            </span>
                        )
                    })}</p>

                </div>
            </div>

        </div>
    )

}
export default RestaurantCard;
