import React, { Component } from 'react';
import jwt_decode from 'jwt-decode';
import axios from 'axios';
import Card from './FavoritePage/FavoriteCard'



export default class Favorite extends Component {


    constructor(props) {
        super(props);
        this.state = { 
            favorites: [],
        }
    }

    componentDidMount() {
        const token = localStorage.usertoken
        const decoded = jwt_decode(token)

        axios.get('http://localhost:5000/users/favorite/' + decoded.id)
            .then(response => {
                // console.log(response.data)
                const favorites = response.data;
                this.setState({ favorites });
            })
            .catch(error => {
                console.log(error)
            })
    }

    GetRestaurant(rid) {
        return axios.get('http://localhost:5000/restaurants/'+rid)
            .then(response => {
                // console.log(response.data)
                this.CreateCart(response.data);
            })
    }

    FavoriteList(){
        return this.state.favorites.map(currentfavorite =>{
             this.GetRestaurant(currentfavorite.rid)
        });
    }

    CreateCart(response){
        console.log(response)
        return <Card key= {response.rid} favor={response}/>
    }
