import React, { Component } from 'react';
import jwt_decode from 'jwt-decode';
import axios from 'axios';
import Card from './FavoriteCard'



export default class Favorite extends Component {
    constructor(props) {
        super(props);
        this.state = { favorites: [] }
    }

    componentDidMount() {
        const token = localStorage.usertoken
        const decoded = jwt_decode(token)

        axios.get('http://localhost:5000/users/favorite/' + decoded.id)
            .then(response => {
                // console.log(response.data)
                const favorites = response.data;
                this.setState({ favorites });
            })
            .catch(error => {
                console.log(error)
            })
    }

    // GetRestaurant() {
    //     return axios.get('http://localhost:5000/restaurants/23')
    //         .then(response => {
    //             console.log(response)
    //         })
    // }


    FavoriteList(){
        return this.state.favorites.map(currentfavorite =>{
            return <Card key = {currentfavorite.event_id} favor= {currentfavorite}/>
        });
    }

    render() {
        return (
            <div>
                <h1>this is the page for user Restaurant Profile</h1>
                <div className='row'>
                    {this.FavoriteList()}
                </div>

            </div>
        );
    }
}
