import React, { Component } from 'react';
import jwt_decode from 'jwt-decode'
import { Link } from 'react-router-dom';
import axios from "axios"
import style from './eventTemplate.module.css'


export default class EventTemplet extends Component {
    constructor() {
        super()
        this.state = {
            event_id: '',
            rid: '',
            event_title: '',
            event_date: '',
            start_time: '',
            end_time: '',
            description: '',
            event_picture: ''
        }
    }

    componentDidMount() {
        const token = localStorage.usertoken
        const decoded = jwt_decode(token)
        axios.get('http://localhost:5000/restaurants/events/event/' + this.props.match.params.id)
            .then(response => {
                this.setState({
                    rid: response.data.rid,
                    event_title: response.data.event_title,
                    event_date: response.data.event_date,
                    start_time: response.data.start_time,
                    end_time: response.data.end_time,
                    description: response.data.description,
                    event_picture: response.data.event_picture
                })
            })
    }

    render() {
        return (
            <div className="container">

                <img
                    src={this.state.event_picture}
                    alt='customer profile'
                />

                <h1 className="text-center">{this.state.event_title}</h1>
                <span>
                    <Link to={"/restaurant/eventedit/" + this.props.match.params.id}>Edit</Link>
                </span>
                <h3 >{this.state.event_date} </h3>
                <h3 >{this.state.start_time} - {this.state.end_time}</h3>
                <h5>{this.state.description}</h5>

            </div>
        )
    }
}
